import { HfInference } from "npm:@huggingface/inference"
// import { HfInference } from '@huggingface/inference'
import { InferenceAPIClient } from '@huggingface/inference';


const hf = new HfInference('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')


// Create a client instance
const client = new InferenceAPIClient({ apiKey: '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' });

// Function to generate text using the BioMistral 7B model
export const generateText = async (prompt, maxTokens = 50) => {
  try {
    const response = await client.generate({
      modelId: 'mdpeterson/biomistral-7b',
      prompt,
      maxTokens,
    });

    return response.data;
  } catch (error) {
    throw new Error('Error generating text:', error);
  }
};
