import React from 'react';
import { useLocation } from 'react-router-dom';

function Home() {
  const location = useLocation();

  const isHomePage = location.pathname === '/home';

  const backgroundStyle = {
    backgroundImage: isHomePage ? "url('https://thumbs.dreamstime.com/z/informational-flyer-nursing-home-building-flat-poster-group-elderly-people-walks-near-banner-meet-visitors-street-cartoon-154355370.jpg')" : 'none',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    height: '100vh', // Adjust height as needed
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    filter: isHomePage ? 'blur(5px)' : 'none' // Adjust blur amount as needed
  };

  const contentStyle = {
    zIndex: 1, // Ensure content is above the blurred background
    position: 'relative',
    color: 'white' // Adjust text color for better visibility
  };

  return (
    <div style={backgroundStyle}>
      <div style={contentStyle}>
        <h1>This is Home Page</h1>
      </div>
    </div>
  );
}

export default Home;
